operation:
query ($id: Int!) {
  employeeAsList(id: $id) {
    details {
      origin: nationality
      firstname: forename
      lastname: surname
      surname
    }
    hobbies {
      ... on Exercise {
        sport:category
      }
      ... on Flying {
        approved: planeModels
        approvedInYears: yearsOfExperience
      }
    }
    type: tag
    role {
      ... on Engineer {
        engTitle: title
        engDepts: departments
        reports: employees {
          id
          details {
            forename
            surname          }
        }
      }
      ... on Marketer {
        title
        reports: employees {
          id
          details {
            forename
            surname
          }
        }
      }
    }
  }
}
